# a class function that orders 2 coffees (capuccino, espresso) and a bigmac burger
class order:
    #attributes defined in a constractor
    def __init__(self,coffee1,coffee2,burger):
        self.coffee1 = coffee1
        self.coffee2 = coffee2
        self.burger = burger
    #method behaviours of object
    def place_order(self):
        print(f"Can i please have 2 coffees one {self.coffee1} and {self.coffee2} with a bigmac extra sauce {self.burger}")
        print("thank you!")

#creating our object\instanciation
real_order = order("capuccino", "epresso", "burger")
#calling our object method
real_order.place_order()